// NewUserContent.js
import React, { useState } from 'react';
import axios from 'axios';

const NewUserContent = () => {
  const [cardNumber, setCardNumber] = useState('');
  const [idNumber, setIdNumber] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [expiryDate, setExpiryDate] = useState('');

  const handleAddCard = async () => {
    try {

      // Make a request to your backend to save the card details
      const response = await axios.post('/auth/register-card', {
        cardNumber,
        idNumber,
        phoneNumber,
        cardName,
        expiryDate,
      });

      // Handle the response 

      alert('Card added successfully!',re);
    } catch (error) {
      console.error('Error adding card:', error);

      alert('Failed to add card. Please try again.');
    }
  };

  return (
    <div>
      <h1>Welcome to the Dashboard! Please add your card details.</h1>
      <form>
        <label>
          Card Number:
          <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} />
        </label>
        <br />
        <label>
          ID Number:
          <input type="text" value={idNumber} onChange={(e) => setIdNumber(e.target.value)} />
        </label>
        <br />
        <label>
          Phone Number:
          <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
        </label>
        <br />
        <label>
          Name on Card:
          <input type="text" value={cardName} onChange={(e) => setCardName(e.target.value)} />
        </label>
        <br />
        <label>
          Expiry Date:
          <input type="text" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} />
        </label>
        <br />
        <button type="button" onClick={handleAddCard}>
          Add Card
        </button>
      </form>
    </div>
  );
};

export default NewUserContent;
