import './cards.css';
import {cards} from '../data/cardsData'
import {FaChevronRight} from 'react-icons/fa';
import QuickActions from '../components/quickActions'
import { useState } from 'react';

const Cards = () => {
    return (
     
        <div className='cards'>
          <div className='title-container'>
                    <h3 className='title'>Cards</h3>  
                </div>
                <div className='button-container'>
                <button className='to-right-arrowBtn' type='button'><FaChevronRight/></button>
                </div>
            <div className='card-container'>    
               {cards.map((card)=>(
                <div className='card-details'  key={card.id}>
                <p className='card-type'>{card.cardType}</p>
                <h3 className='card-balance'>{card.value}</h3>
                <div className='card-digits'>
                <span className='card-number'>**** **** ****{card.cardNo}</span>
                <span className='card-exDate'>{card.expiryDate}</span>
                </div>
               </div> ))
               }    
            </div>
        
            </div>  

      
    )
}

export default Cards;
