// TopupForm.js

import React, { useState } from 'react';

const TopupForm = () => {
  // State to manage selected sender type (bank or card)
  const [selectedSender, setSelectedSender] = useState(''); // Initialize with an empty string
  const [selectedCard, setSelectedCard] = useState(''); // Initialize with an empty string

  // State to manage input and currency
  const [transferAmount, setTransferAmount] = useState('');
  const [selectedCurrency, setSelectedCurrency] = useState('USD');

  const handleSenderChange = (e) => {
    setSelectedSender(e.target.value);
  };

  const handleTransferAmountChange = (e) => {
    setTransferAmount(e.target.value);
  };

  const handleCurrencyChange = (e) => {
    setSelectedCurrency(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Selected Sender:', selectedSender);
    console.log('Selected Card:', selectedCard);
    console.log('Transfer Amount:', transferAmount);
    console.log('Selected Currency:', selectedCurrency);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Sender:
          <select
            className="sender-select"
            value={selectedSender}
            onChange={handleSenderChange}
          >
           
            <option value="visa1">VISA 1</option>
            <option value="visa2">VISA 2</option>
          </select>
        </label>
        <label>
          Card:
          <select
            className="card-select"
            value={e}
            onChange={handleSenderChange}
          >
           
            <option value="visa1">VISA 1</option>
            <option value="visa2">VISA 2</option>
          </select>
        </label>
      </div>
      <div>
        <label>
          Transfer amount:
          <input
            type="text"
            className="username-input"
            placeholder="Transfer amount"
            value={transferAmount}
            onChange={handleTransferAmountChange}
          />
        </label>
      </div>
      <div>
        <label>
          Select currency:
          <select
            className="currency-select"
            value={selectedCurrency}
            onChange={handleCurrencyChange}
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            {/* Add more currency options as needed */}
          </select>
        </label>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default TopupForm;
