import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../components/loginSignup.css';
import loginImage from '../assets/login_img.jpg';
import axios from 'axios';

const Login = ({ onLogin }) => {

  const navigate = useNavigate();

  const [email, setEmail]=useState('');
  const [password, setPassword] = useState('');
 
  const handleLogin = async (e) => {

   e.preventDefault();
   try {
    
    const response = await axios.post('/auth/login', {
      email,
      password,
    });

    if (response.data.token) {
      const userResponse = await axios.get('/auth/new-user');

      if (userResponse.data.isNewUser) {
        // If the user is new, navigate to NewUserContent
        navigate('/add-card');
      } else {
        // If the user has registered a card, navigate to Dashboard
        navigate('/dashboard');
      }
    } else {
      // Handle login failure, show an error message, etc.
      alert('Wrong credentials');
    }
  } catch (error) {
    console.error('Error logging in:', error);
    // Handle the error, show an error message, etc.
    alert('Failed to log in. Please try again.');
  }
};
  return (
    <div className='main-container'>
    <form className='form-container' action='POST'>
      <h2>Login</h2>
      <input
        className='username-input'
        type="text"
        placeholder="Enter your email address"
        value={email}
        onChange={(e) => setEmail(e.target.value)}/>
      <input
        className='password-input'
        type="password"
        placeholder="Enter your password"
        value={password}
        onChange={(e) => setPassword(e.target.value)} />
      <p>Forgot Password?</p>
      <div className='btn-cont'>
        <button className='login-btn' onClick={handleLogin}>
          Login
        </button>
      </div>
      <p>
        Don't have an account?{' '}
        <Link to="/signup" className='signup-btn'>
          <button className='signup-btn'>Sign up</button>
        </Link>
      </p> 
      </form>
      <div className='image-container'>
        <img
          src={loginImage}
          alt="Background"
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
      </div>
    </div>
  );
};

export default Login;
