// Signup.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './loginSignup.css';

const Signup = ({ isAuthenticated, onLogin }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordMatchError, setPasswordMatchError] = useState(false);

  const handleSignup = async () => {
    if (password !== confirmPassword) {
      setPasswordMatchError(true);
      return;
    }
    try {
      // Make a request to the backend to register the user
      const response = await axios.post('/api/signup', { username, password });

      // Assuming the backend sends a token upon successful registration
      const token = response.data.token;

      // Call the onLogin function passed from the parent component
      onLogin(token);
    } catch (error) {
      console.error('Sign up failed', error);
    }
  };

  const handleLogin = async () => {
    try {
      // Make a request to the backend to authenticate the user
      const response = await axios.post('/api/login', { username, password });
      
      // Assuming the backend sends a token upon successful login
      const token = response.data.token;

      // Call the onLogin function passed from the parent component
      onLogin(token);
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  // Redirect to the dashboard if the user is already authenticated
  if (isAuthenticated) {
    navigate('/dashboard');
    return null;
  }

  return (
    <div className='main-container'>
      <h2>Sign up</h2>
      <input
        className='username-input'
        type="text"
        placeholder="Enter your username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className='password-input'
        type="password"
        placeholder="Enter your password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        className='password-confirm-input'
        type="password"
        placeholder="Confirm your password"
        value={confirmPassword}
        onChange={(e) => {
          setConfirmPassword(e.target.value);
          setPasswordMatchError(false);
        }}
      />
      {passwordMatchError && <p className='error-message'>Passwords do not match</p>}
      <div className='btn-cont'>
        <button className='signupbtn' onClick={handleSignup}>Sign up</button>
      </div>
      <p>
        Already have an account?{' '}
        <Link to="/login">
          <button className='loginbtn' onClick={handleLogin}>Login</button>
        </Link>
      </p>
    </div>
  );
};

export default Signup;
