// Signup.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './loginSignup.css';
import loginImage from '../assets/login_img.jpg';




const Signup = ({ isAuthenticated, onLogin }) => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');

  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordMatchError, setPasswordMatchError] = useState(false);

  const handleSignup = async () => {
    if (password !== confirmPassword) {
      setPasswordMatchError(true);
      return;
    }
    try {
      // Make a request to the backend to register the user
      const response = await axios.post('/auth/signup', { name,surname, password });

      // Assuming the backend sends a token upon successful registration
      const token = response.data.token;

      // Call the onLogin function passed from the parent component
      onLogin(token);
    } catch (error) {
      console.error('Sign up failed', error);
    }
   
  };

  const handleLogin = async () => {
    try {
      // Make a request to the backend to authenticate the user
      const response = await axios.post('/api/login', { name, password });
      
      // Assuming the backend sends a token upon successful login
      const token = response.data.token;

      // Call the onLogin function passed from the parent component
      onLogin(token);
    } catch (error) {
      console.error('Login failed', error);
    }
  };

  // Redirect to the dashboard if the user is already authenticated
  if (isAuthenticated) {
    navigate('/dashboard');
    return null;
  }

  return (
    <div className='main-container'>
    <form className='form-container'>
      <h2>Sign up</h2>
      <input
        className='username-input'
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
       <input
        className='surname-input'
        type="text"
        placeholder="Enter your surname"
        value={surname}
        onChange={(e) => setSurname(e.target.value)}
      />
      <input
        className='password-input'
        type="password"
        placeholder="Create a password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        className='password-confirm-input'
        type="password"
        placeholder="Confirm the password"
        value={confirmPassword}
        onChange={(e) => {
          setConfirmPassword(e.target.value);
          setPasswordMatchError(false);
        }}
      />
      {passwordMatchError && <p className='error-message'>Passwords do not match</p>}
      <div className='btn-cont'>
        <button className='signupbtn' onClick={handleSignup}>Signup</button>
      </div>
      <p>
        Already have an account?{' '}
        <Link to="/login">
          <button className='loginbtn' onClick={handleLogin}>Login</button>
        </Link>
      </p>
      </form>
      <div className='image-container'>
        <img
          src={loginImage}
          alt="Background"
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
      </div>
    </div>
  );
};

export default Signup;
