// NewUserContent.js
import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const NewUserContent = () => {

  const [data,setData]=useState({
    cardNumber:'',
    expiryDate:'',
    CVV:'',
    phoneNumber:'',
  })


  const handleAddCard = async () => {
     const{cardNumber,expiryDate,CVV,phoneNumber,}=data;
    try {
       
     
      const response = await axios.post('/register-card', {
        cardNumber,expiryDate,CVV,phoneNumber,
      });

    
      alert('Card added successfully!',response);
    } catch (error) {
      console.error('Error adding card:', error);

      alert('Failed to add card. Please try again.');
    }
  };

  return (
    <div>
      <h1>Welcome to the Dashboard! Please add your card details.</h1>
      <form>
        <label>
          Card Number:
          <input type="text" value={data.cardNumber}  onChange={(e)=>setData({...data, cardNumber:e.target.value})} />
        </label>
        <br />
       
        <br />
        <label>
          Phone Number:
          <input type="text" value={data.phoneNumber} onChange={(e) => setData({...data, phoneNumber:e.target.value})} />
        </label>
        <br />
        <label>
          Expiry Date:
          <input type="text" value={data.expiryDate} onChange={(e) => setData({...data, expiryDate:e.target.value})} />
        </label>
        <br />
        <button type="submit" onClick={handleAddCard}>
          Add Card
        </button>
      </form>
    </div>
  );
};

export default NewUserContent;
