// TopupForm.js

import React, { useState } from 'react';

const TopupForm = () => {
  // State to manage checkboxes
  const [isSenderBank, setIsSenderBank] = useState(false);
  const [isSenderCard, setIsSenderCard] = useState(false);

  // State to manage input and currency
  const [transferAmount, setTransferAmount] = useState('');
  const [selectedCurrency, setSelectedCurrency] = useState('USD');

  const handleSenderBankChange = () => {
    setIsSenderBank(!isSenderBank);
    // If sender is a bank, uncheck the card option
    setIsSenderCard(false);
  };

  const handleSenderCardChange = () => {
    setIsSenderCard(!isSenderCard);
    // If sender is a card, uncheck the bank option
    setIsSenderBank(false);
  };

  const handleTransferAmountChange = (e) => {
    setTransferAmount(e.target.value);
  };

  const handleCurrencyChange = (e) => {
    setSelectedCurrency(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Sender (Bank):', isSenderBank);
    console.log('Sender (Card):', isSenderCard);
    console.log('Transfer Amount:', transferAmount);
    console.log('Selected Currency:', selectedCurrency);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          <input
            type="checkbox"
            className="sender-checkbox"
            checked={isSenderBank}
            onChange={handleSenderBankChange}
          />
          Sender (Bank)
        </label>
      </div>
      <div>
        <label>
          <input
            type="checkbox"
            className="card-checkbox"
            checked={isSenderCard}
            onChange={handleSenderCardChange}
          />
          Sender (Card)
        </label>
      </div>
      <div>
        <label>
          Transfer amount:
          <input
            type="text"
            className="username-input"
            placeholder="Transfer amount"
            value={transferAmount}
            onChange={handleTransferAmountChange}
          />
        </label>
      </div>
      <div>
        <label>
          Select currency:
          <select
            className="currency-checkbox"
            value={selectedCurrency}
            onChange={handleCurrencyChange}
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            {/* Add more currency options as needed */}
          </select>
        </label>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default TopupForm;
