// NewUserContent.js
import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Sidebar from './sidebar';

const Container = styled.div`
  display: flex;
  

`;

const FormContainer = styled.div`
  margin-left: 30px;
  margin-top: 40px;
`;

const Form = styled.form`
  padding: 18px 100px 24px 40px;
  background: rgba(10, 144, 144, 0.107);
  border-radius: 10px;
  box-shadow: 5px 5px 12px rgba(0, 0, 0, 0.201);
  cursor: pointer;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 10px;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-bottom: 10px;
`;

const Button = styled.button`
  background-color: rgba(10, 144, 144);
  color: white;
  border: none;
  border-radius: 5px;
  padding: 10px;
  cursor: pointer;
`;

const NewUserContent = () => {
  const [data, setData] = useState({
    cardNumber: '',
    expiryDate: '',
    CVV: '',
    phoneNumber: '',
  });

  const handleAddCard = async () => {
    const { cardNumber, expiryDate, CVV, phoneNumber } = data;
    try {
      const response = await axios.post('/register-card', {
        cardNumber,
        expiryDate,
        CVV,
        phoneNumber,
      });
      alert('Card added successfully!', response);
    } catch (error) {
      console.error('Error adding card:', error);
      alert('Failed to add card. Please try again.');
    }
  };

  return (
    <Container>
      <Sidebar />
      <FormContainer>
        <Form onSubmit={handleAddCard}>
          <h3>Add a card</h3>
          <Label>
            Card Number:
            <Input
              type="text"
              value={data.cardNumber}
              onChange={(e) => setData({ ...data, cardNumber: e.target.value })}
            />
          </Label>
          <Label>
            Phone Number:
            <Input
              type="text"
              value={data.phoneNumber}
              onChange={(e) => setData({ ...data, phoneNumber: e.target.value })}
            />
          </Label>
          <Label>
            Expiry Date:
            <Input
              type="text"
              value={data.expiryDate}
              onChange={(e) => setData({ ...data, expiryDate: e.target.value })}
            />
          </Label>
          <Button type="submit">Add Card</Button>
        </Form>
      </FormContainer>
    </Container>
  );
};

export default NewUserContent;
