// NewUserContent.js
import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const NewUserContent = () => {

  const [data,setData]=useState({

    cardNumber:'',
    expiryDate:'',
    CVV:''
  })
  const [cardNumber, setCardNumber] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [nameOnCard, setCardName] = useState('');
  const [expiryDate, setExpiryDate] = useState('');

  const handleAddCard = async () => {
    try {
  
      const response = await axios.post('/register-card', {
        cardNumber,expiryDate,CVV,phoneNumber,
      });

    
      alert('Card added successfully!',response);
    } catch (error) {
      console.error('Error adding card:', error);

      alert('Failed to add card. Please try again.');
    }
  };

  return (
    <div>
      <h1>Welcome to the Dashboard! Please add your card details.</h1>
      <form>
        <label>
          Card Number:
          <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} />
        </label>
        <br />
       
        <br />
        <label>
          Phone Number:
          <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
        </label>
        <br />
        <label>
          Name on Card:
          <input type="text" value={nameOnCard} onChange={(e) => setCardName(e.target.value)} />
        </label>
        <br />
        <label>
          Expiry Date:
          <input type="text" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} />
        </label>
        <br />
        <button type="button" onClick={handleAddCard}>
          Add Card
        </button>
      </form>
    </div>
  );
};

export default NewUserContent;
