// Signup.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './loginSignup.css';
import loginImage from '../assets/login_img.jpg';


const Signup = (e) => {
  e.preventDefault();

  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email,setEmail]=useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [passwordMatchError, setPasswordMatchError] = useState(false);

  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      if (password !== confirmPassword) {
        setPasswordMatchError(true);
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/signup',
        { name, surname, email, password },
        { headers: { 'Content-Type': 'application/json' } }
      );

      if (response.status === 201) {
        alert('Registration successful!');
        navigate('/login');
      }
    } catch (error) {
      alert('Registration failed');
      console.error('Sign up failed', error);
    }
  };


  // const handleLogin = async () => {
  //   try {
      
  //     const response = await axios.post('/auth/login', { name, password });
  //     const token = response.data.token;
  //     onLogin(token);
  //   } catch (error) {
  //     console.error('Login failed', error);
  //   }
  // };

  // if (isAuthenticated) {
  //   navigate('/dashboard');
  //   return null;
  // }

  return (
    <div className='main-container'>
    <form className='form-container' onSubmit={handleSignup}>
      <h2>Sign up</h2>
      <input
        className='username-input'
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
       <input
        className='surname-input'
        type="text"
        placeholder="Enter your surname"
        value={surname}
        onChange={(e) => setSurname(e.target.value)}
      />  
       <input
        className='email-input'
        type="text"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        className='password-input'
        type="password"
        placeholder="Create a password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        className='password-confirm-input'
        type="password"
        placeholder="Confirm the password"
        value={confirmPassword}
        onChange={(e) => {
          setConfirmPassword(e.target.value);
          setPasswordMatchError(false);
        }}
      />
      {passwordMatchError && <p className='error-message'>Passwords do not match</p>}
      <div className='btn-cont'>
        <button className='signupbtn' onClick={handleSignup}>Signup</button>
      </div>
      <p>
        Already have an account?{' '}
        <Link to="/login">
          <button className='loginbtn'>Login</button>
        </Link>
      </p>
      </form>
      <div className='image-container'>
        <img
          src={loginImage}
          alt="Background"
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
      </div>
    </div>
  );
};

export default Signup;
