import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '.././loginSignup.css';


const Login = ({ onLogin }) => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    // You would typically send the login data to a server for authentication.
    // For now, let's assume a simple check with a hardcoded user.
    if (username === 'sampleUser' && password === 'password') {
      navigate('/dashboard'); // Redirect to the dashboard on successful login
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className='main-container'>
      <h2>Login</h2>
      <input
        className='username-input'
        type="text"
        placeholder="Enter your username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}/>
      <input
        className='password-input'
        type="password"
        placeholder="Enter your password"
        value={password}
        onChange={(e) => setPassword(e.target.value)} />
      <p>Forgot Password?</p>
      <div className='btn-cont'>
        <button className='login-btn' onClick={handleLogin}>
          Login
        </button>
      </div>
      <p>
        Don't have an account?{' '}
        <Link to="/signup" className='signup-btn'>
          <button className='signup-btn'>Sign up</button>
        </Link>
      </p>
    </div>
  );
};

export default Login;

// The rest of your Login component can go here, if needed.
// For example, you might want to include some container logic or routing logic.
